import { expect } from 'chai';
import auditActions from '../../audit/auditAction';

describe('Audit actions', () => {
  it('Should have 1 action', () => {
    expect(auditActions).to.have.key('save_user');
    expect(auditActions.save_user).to.eq('SAVE_USER');
  });
});

const auditActions = {
	save_user: 'SAVE_USER',
};

export default auditActions;

import { expect } from 'chai';
import app from '../../server';
import request from 'supertest';
import { User } from '../../model/user';

const store = new User();

describe('User handler', () => {
  let id: string, token: string;
  describe('POST /users/signup', () => {
    it('Should create new user', async () => {
      const user = {
        email: 'test@test.com',
        password: '123456',
        confirmPassword: '123456',
        name: 'Bassel',
      };
      const response = await request(app)
        .post('/users/signup')
        .send(user)
        .set('Accept', 'application/json');
      expect(response.status).eq(201);
      expect(response.body.message).eq('user created');

      id = response.body.user.id;
    });

    it('Should throw an error name must be unique', async () => {
      const user = {
        email: 'test2@test.com',
        password: '123456',
        confirmPassword: '123456',
        name: 'Bassel',
      };
      const response = await request(app)
        .post('/users/signup')
        .send(user)
        .set('Accept', 'application/json');
      expect(response.status).eq(422);
      expect(response.body.errors.msg).eq(
        'E-mail and Username must be unique.'
      );
    });

    it('Should throw validation error', async () => {
      const user = {
        email: 'test10',
        password: '123456',
        confirmPassword: '123456',
        name: 'Bassel10',
      };
      const response = await request(app)
        .post('/users/signup')
        .send(user)
        .set('Accept', 'application/json');
      expect(response.status).eq(422);
      expect(response.body.errors.msg).eq('Please provide a valid email.');
    });
  });

  describe('POST /users/login', () => {
    // HERE  before confirming email
    it('Should reject login before email confirmation', async () => {
      const user = {
        email: 'test@test.com',
        password: '123456',
      };
      const response = await request(app)
        .post('/users/login')
        .send(user)
        .set('Accept', 'application/json');
      expect(response.status).eq(401);
      expect(response.body.error).eq('Please confirm your email.');
    });

    // HERE  after confirming email
    it('Should login user sucessfully', async () => {
      const user = {
        email: 'test@test.com',
        password: '123456',
      };
      await store.confirmUser(id);

      const response = await request(app)
        .post('/users/login')
        .send(user)
        .set('Accept', 'application/json');
      expect(response.status).eq(200);
      expect(response.body.name).eq('Bassel');
      expect(response.headers['set-cookie'][0]).to.be.a('string');
      token = response.body.accessToken;
    });

    it('Should reject login, invalid account', async () => {
      const user = {
        email: 'test@test.com',
        password: '000000',
      };
      const response = await request(app)
        .post('/users/login')
        .send(user)
        .set('Accept', 'application/json');
      expect(response.status).eq(404);
      expect(response.body.error).eq('Invalid email or password');
    });
  });

  describe('POST /users/forgot-password', () => {
    it('Should confirm an E-mail is sent when giving registered email', async () => {
      const response = await request(app)
        .post('/users/forgot-password')
        .send({ email: 'test@test.com' })
        .set('Accept', 'application/json');
      expect(response.status).eq(200);
      expect(response.body.message).eq('An E-mail has been sent.');
    });

    it('Should deny that the user exists if email not registered', async () => {
      const response = await request(app)
        .post('/users/forgot-password')
        .send({ email: 'notfound@test.com' })
        .set('Accept', 'application/json');
      expect(response.status).eq(404);
      expect(response.body.error).eq("User doesn't exist..");
    });
  });

  describe('POST /users/check-reset', () => {
    it('Should give an error if the reset code is not valid', async () => {
      // IMPORTANT  the code is only 6 digits
      const response = await request(app)
        .post('/users/check-reset')
        .send({ code: 111111 })
        .query({ id, token: 'dummy-token' })
        .set('Accept', 'application/json');
      expect(response.status).eq(404);
      expect(response.body.error).eq(
        'Expired password reset token or wrong code'
      );
    });
  });

  describe('POST /users/reset-password', () => {
    it('Should give an error, when the reset code is not verified', async () => {
      const response = await request(app)
        .post('/users/reset-password')
        .send({ password: '123456', confirmPassword: '123456' })
        .query({ id });
      expect(response.status).eq(403);
      expect(response.body.error).eq(
        'Password reset code is not verified you are not allowed to change password.'
      );
    });
  });

  describe('GET /users/:author', () => {
    it('Should return user info, if jwt provided and username', async () => {
      const response = await request(app)
        .get('/users/Bassel')
        .set('Authorization', 'Bearer ' + token)
        .expect('Content-Type', /json/);
      expect(response.status).eq(200);
      expect(response.body.message).eq('retrived the user');
    });

    it('Should prevent from getting user if there is no jwt', async () => {
      const response = await request(app)
        .get('/users/Bassel')
        .expect('Content-Type', /json/);
      expect(response.status).eq(403);
      expect(response.body.error).eq('No token was provided');
    });

    it("Should not get user cause it doesn't exists", async () => {
      const response = await request(app)
        .get('/users/Bassel2')
        .set('Authorization', 'Bearer ' + token)
        .expect('Content-Type', /json/);
      expect(response.status).eq(404);
      expect(response.body.error).eq("couldn't find user Bassel2");
    });
  });
});

import express, { Request, Response, NextFunction } from 'express';
import cors from 'cors';
import cookieParser from 'cookie-parser';
import swaggerUI from 'swagger-ui-express';
import dotenv from 'dotenv';
import morgan from 'morgan';

import swaggerDocument from '../swagger.json';
import APIError from './Error/ApiError';
import userRoutes from './handler/user';
import postRoutes from './handler/post';
import commentsRoutes from './handler/comment';
import refreshTokenRoute from './handler/refreshToken';
import oauth2Routes from './handler/oauth2';

dotenv.config();

const app: express.Application = express();

const port = process.env.PORT || 3000;

app.use(cors({ credentials: true, origin: 'http://localhost:5173' }));

app.use(morgan('dev'));
app.use(cookieParser(process.env.COOKIE_SECRET));
app.use(express.json());
app.use('/uploads', express.static('uploads'));
app.use('/api-docs', swaggerUI.serve, swaggerUI.setup(swaggerDocument));

userRoutes(app);
postRoutes(app);
commentsRoutes(app);
refreshTokenRoute(app);
oauth2Routes(app);

app.all('*', (req, res, next) => {
  next(
    new APIError(
      `Couldn't find such a route visit ${req.baseUrl}/api-docs to get more info`,
      404,
      "this route couldn't be found",
      true
    )
  );
});

app.use((error: APIError, req: Request, res: Response, next: NextFunction) => {
  const message = error.name;
  res.status(error.statusCode).json({ error: message });
});

app.listen(port, () => {
  console.log(`server is running up on http://localhost:${port}`);
});

export default app;

import BaseError from './Error';

class APIError extends BaseError {}

export default APIError;

class BaseError extends Error {
  name: string;

  statusCode: number;

  description: string;

  isOperational: boolean;

  constructor(
    name: string,
    statusCode: number,
    description: string,
    isOperational: boolean
  ) {
    super(description);
    // Object.prototype(this, new.target.prototype);
    this.statusCode = statusCode;
    this.name = name;
    this.description = description;
    this.isOperational = isOperational;
    Error.captureStackTrace(this);
  }
}

export default BaseError;

import { Application, Request, Response, NextFunction } from 'express';
import crypto from 'crypto';
import { User } from '../model/user';
import signToken from '../util/signToken';
import validation from '../middleware/validation';
import verifyToken from '../middleware/verifyToken';

import APIError from '../Error/ApiError';

import dotenv from 'dotenv';
import {
  validateUserUpdate,
  validateUserCreate,
  validateUserAuthenticate,
  validateUserForgetPassword,
  validateUserCheckReset,
  validateUserResetPassword,
} from '../util/validators/userValidators';

import { compare, hash } from 'bcrypt';
import mail from '../util/mailing';
import { Token } from '../model/token';
import { verify } from 'jsonwebtoken';
dotenv.config();

const store = new User();
const reset = new Token();

const {
  JWT_SECRET,
  JWT_ACCESS_EXPIRY,
  JWT_REFRESH_SECRET,
  JWT_REFRESH_EXPIRY,
  SR,
  JWT_EMAIL,
  JWT_EMAIL_EXPIRY,
} = process.env;

const index = async (_req: Request, res: Response): Promise<void> => {
  try {
    const users = await store.index();
    res.json({ message: 'retrived users sucessfully', data: users });
  } catch (err) {
    throw new Error(`couldn't get users, ${err}`);
  }
};

const show = async (
  req: Request,
  res: Response,
  next: NextFunction
): Promise<void> => {
  try {
    const user = await store.show(req.params.author);
    if (!user.author) {
      return next(
        new APIError(
          `couldn't find user ${req.params.author}`,
          404,
          'failed while trying to get the user',
          true
        )
      );
    }
    res.json({
      message: 'retrived the user',
      author: user.author,
      posts: user.posts,
    });
  } catch (err) {
    throw new Error(`couldn't find user,${req.params.id} , ${err}`);
  }
};

const remove = async (req: Request, res: Response): Promise<void> => {
  try {
    const user = await store.delete(req.params.id);
    res.json({ message: ' the user is deleted', data: user });
  } catch (err) {
    throw new Error(`couldn't delete user,${req.params.id} , ${err}`);
  }
};

const update = async (
  req: Request,
  res: Response,
  next: NextFunction
): Promise<void> => {
  const user = await store.update(req.params.id, req.body.password);
  if (!user) {
    return next(
      new APIError(
        `couldn't update user ${req.params.id}`,
        422,
        'failed while trying to update the user',
        true
      )
    );
  }
  res.json({ message: 'updated the user ', data: user });
};

const create = async (
  req: Request,
  res: Response,
  next: NextFunction
): Promise<void> => {
  const user = await store.create(req.body);
  if (!user) {
    return next(
      new APIError(
        `couldn't create user ${req.params.id}`,
        404,
        'failed while trying to create the user',
        true
      )
    );
  }
  const confirmToken = signToken(
    { id: user.id },
    JWT_EMAIL + '',
    JWT_EMAIL_EXPIRY + ''
  );
  const link = `http://localhost:3000/users/confirm/${confirmToken}`;
  mail(user.email, 'Email Confirmation', link, user.name);
  res.status(201).json({ message: 'user created', user });
};

const authenticate = async (
  req: Request,
  res: Response,
  next: NextFunction
): Promise<void> => {
  try {
    const jwtCookie = req.cookies['refresh-token'];
    const { email, password } = req.body;
    const user = await store.authenticate(email, password);
    if (!user) {
      return next(
        new APIError(
          `Invalid email or password`,
          404,
          'failed to authenticate the user',
          true
        )
      );
    }
    if (user.confirmed === false) {
      return next(
        new APIError(
          `Please confirm your email.`,
          401,
          'User must confirm email to be able to login',
          true
        )
      );
    }
    const { id, name } = user;
    const token = signToken(
      { id, name, email },
      JWT_SECRET + '',
      JWT_ACCESS_EXPIRY + ''
    );
    const refreshToken = signToken(
      { id, name, email },
      JWT_REFRESH_SECRET + '',
      JWT_REFRESH_EXPIRY + ''
    );

    // ? remove the old token if exists in cookie
    let newRefreshTokens = (
      !jwtCookie
        ? user.refreshtoken
        : user.refreshtoken?.filter((rt) => rt !== jwtCookie)
    ) as Array<string>;

    if (jwtCookie) {
      // ! if user logs in, never logs out or use RT (we must check for reuse detection so same refresh token not used in 2 logins or more)

      const foundToken = await store.showByToken(jwtCookie);
      if (!foundToken) {
        // ? now we are sure the token is deleted and was used before
        newRefreshTokens = [];
      }

      res.clearCookie('refresh-token', {
        httpOnly: true,
        maxAge: 24 * 60 * 60 * 1000,
      });
    }
    if (!newRefreshTokens) {
      newRefreshTokens = [];
    }
    await store.storeToken(email, [...newRefreshTokens, refreshToken]);
    res.cookie('refresh-token', refreshToken, {
      httpOnly: true,
      maxAge: 24 * 60 * 60 * 1000,
    });

    res.status(200).json({ name: user.name, accessToken: token });
  } catch (err) {
    throw new Error(`couldn't authenticate user, ${req.body.email} , ${err}`);
  }
};

const forgotPassword = async (
  req: Request,
  res: Response,
  next: NextFunction
): Promise<void> => {
  try {
    const user = await store.validate(req.body.email);
    if (!user) {
      return next(
        new APIError(
          `User doesn't exist..`,
          404,
          'failed to authenticate the user',
          true
        )
      );
    }
    const resetCode = Math.floor(100000 + Math.random() * 900000) + ''; // NOTE  ensuring first digit will never be 0
    const resetToken = crypto.randomBytes(20).toString('hex');
    const hashedToken = await hash(resetToken, Number(SR));
    const hashedCode = crypto
      .createHash('sha512')
      .update(resetCode)
      .digest('hex');

    // !  HERE  instead of removing all previous reset Tokens, ordering tokens and get last one (DESC) and once user reset password we delete all he created before

    await reset.removeToken(user.id + '');
    await reset.createToken(hashedToken, hashedCode, user.id);
    // NOTE saved hashs will be later compared to see if we really got valid request or not, resetToken valid for 15min, check if verified
    const link = `http://localhost:5173/reset?token=${resetToken}&id=${user.id}`;
    mail(user.email, 'Password Reset Request', link, user.name, resetCode);
    res.status(200).send({ message: 'An E-mail has been sent.' });
  } catch (err) {
    // NOTE we may delete all info we saved if an error happened
    console.log(err);
  }
};

const checkResetCode = async (
  req: Request,
  res: Response,
  next: NextFunction
): Promise<void> => {
  try {
    const { token, id } = req.query;
    const storedToken = await reset.getToken(id + '');
    if (!storedToken) {
      return next(
        new APIError(
          `Invalid or expired password reset token`,
          404,
          'failed to authenticate the user',
          true
        )
      );
    }
    if (
      !(await compare(token + '', storedToken.token)) ||
      crypto.createHash('sha512').update(req.body.code).digest('hex') !==
        storedToken.code
    ) {
      return next(
        new APIError(
          `Expired password reset token or wrong code`,
          404,
          'failed to authenticate the user',
          true
        )
      );
    }

    await reset.verifiedCode(id + '');
    res.status(200).send({
      message:
        'The reset code has been verified you are ready to change the password',
    });
  } catch (err) {
    res
      .status(422)
      .json({ message: 'error occured while verifying reset code' });
  }
};

const resetPassword = async (
  req: Request,
  res: Response,
  next: NextFunction
): Promise<void> => {
  try {
    const { id } = req.query;
    const { password } = req.body;
    // HERE  verifying that the user indeed exists
    // const user = await store.getById(id);
    // if (!user) {
    //   return next(
    //     new APIError(
    //       `Cannot find the user with Id: ${id}`,
    //       404,
    //       'failed to get the user',
    //       true
    //     )
    //   );
    // }
    const storedToken = await reset.getToken(id + '');
    if (!storedToken?.verified) {
      return next(
        new APIError(
          `Password reset code is not verified you are not allowed to change password.`,
          403,
          'Not verified reset token',
          true
        )
      );
    }
    await store.update(id + '', password);
    await reset.removeToken(id + '');

    res
      .status(200)
      .send({ message: 'You changed your password successfully.' });
  } catch (err) {
    console.log(err);
  }
};

const confirmEmail = async (
  req: Request,
  res: Response,
  next: NextFunction
): Promise<void> => {
  try {
    // NOTE  we need to validate if the user is confirmed or not
    const { token } = req.params;
    const {
      user: { id },
    } = verify(token, JWT_EMAIL + '') as { user: { id: number } };
    const user = await store.getById(id + '');
    if (!user || user.confirmed) {
      return next(
        new APIError(
          `The Link expired or no user found.`,
          403,
          "the user is trying to confirming an email which is alread confirmed or a user that doesn't exists",
          true
        )
      );
    }
    await store.confirmUser(id + '');
    res.status(302).redirect('http://localhost:5173/login?confirmed=true');
  } catch (err) {
    res.sendStatus(403);
  }
};

const userRoutes = (app: Application): void => {
  // ! Development purpose only
  app.get('/users', index);
  app.delete('/users/:id', remove);
  app.patch('/users/:id', validateUserUpdate(), validation, update);
  // HERE  . our used routes
  app.get('/users/:author', verifyToken, show);
  app.post('/users/signup', validateUserCreate(), validation, create);
  app.post(
    '/users/login',
    validateUserAuthenticate(),
    validation,
    authenticate
  );
  app.post(
    '/users/forgot-password',
    validateUserForgetPassword(),
    validation,
    forgotPassword
  );
  app.post(
    '/users/check-reset',
    validateUserCheckReset(),
    validation,
    checkResetCode
  );
  app.post(
    '/users/reset-password',
    validateUserResetPassword(),
    validation,
    resetPassword
  );
  app.get('/users/confirm/:token', confirmEmail);
};

export default userRoutes;

import Client from '../database/client';
import dotenv from 'dotenv';
import bcrypt from 'bcrypt';
import { TPost } from './post';

dotenv.config();
const { PEPPER, SR } = process.env;

export type TUser = {
  id?: number;
  email: string;
  password: string;
  name?: string;
  confirmed?: boolean;
  refreshtoken?: string[];
};

export class User {
  async index(): Promise<TUser[]> {
    try {
      const conn = await Client.connect();
      const sql = 'SELECT email,name FROM users';
      const result = await conn.query(sql);
      conn.release();
      return result.rows;
    } catch (err) {
      throw new Error(`Cannot get users ${err}`);
    }
  }

  async show(author: string): Promise<{ author: TUser; posts: TPost[] }> {
    try {
      const conn = await Client.connect();
      const sql = 'SELECT name,email FROM users WHERE name=$1';
      const result = await conn.query(sql, [author]);
      const sql2 =
        'SELECT * FROM posts p WHERE author=$1 ORDER BY p.id DESC LIMIT 6';
      const result2 = await conn.query(sql2, [author]);
      conn.release();
      return { author: result.rows[0], posts: result2.rows };
    } catch (err) {
      throw new Error(`Cannot get user ${author}, ${err}`);
    }
  }

  async getById(id: string): Promise<TUser> {
    try {
      const conn = await Client.connect();
      const sql = 'SELECT * FROM users WHERE id=$1';
      const result = await conn.query(sql, [id]);
      conn.release();
      return result.rows[0];
    } catch (err) {
      throw new Error(`Cannot get user ${id}, ${err}`);
    }
  }

  async update(id: string, password: string): Promise<TUser | undefined> {
    try {
      const conn = await Client.connect();
      const sql =
        'UPDATE users SET password=$1,refreshtoken=$2 WHERE id=$3 RETURNING *';
      const hashedPassword = await bcrypt.hash(password + PEPPER, Number(SR));
      const result = await conn.query(sql, [hashedPassword, [], id]);
      conn.release();
      return result.rows[0];
    } catch (err) {
      console.log(`Cannot get user ${id}, ${err}`);
    }
  }

  async confirmUser(id: string): Promise<void> {
    try {
      const conn = await Client.connect();
      const sql = 'UPDATE users SET confirmed=TRUE WHERE id=$1';
      await conn.query(sql, [id]);
      conn.release();
    } catch (err) {
      console.log(`Couldn't confirm user ${id}, ${err}`);
    }
  }

  async delete(id: string): Promise<TUser> {
    try {
      const conn = await Client.connect();
      const sql = 'DELETE FROM users WHERE id=$1 RETURNING *';
      const result = await conn.query(sql, [id]);
      conn.release();
      return result.rows[0];
    } catch (err) {
      throw new Error(`Cannot delete user ${id}, ${err}`);
    }
  }

  async create(user: TUser): Promise<TUser> {
    try {
      const conn = await Client.connect();
      const sql =
        'INSERT INTO users(email,password,name) VALUES ($1,$2,$3) RETURNING email,name,id';
      const hashedPassword = await bcrypt.hash(
        user.password + PEPPER,
        Number(SR)
      );
      const result = await conn.query(sql, [
        user.email,
        hashedPassword,
        user.name,
      ]);
      conn.release();
      return result.rows[0];
    } catch (err) {
      throw new Error(`Cannot create user ${user.name} , ${err}`);
    }
  }

  async authenticate(email: string, password: string): Promise<TUser | null> {
    try {
      const conn = await Client.connect();
      const sql = 'SELECT * FROM users WHERE email=$1';
      const result = await conn.query(sql, [email]);
      conn.release();
      if (result.rows.length) {
        const user = result.rows[0];
        if (await bcrypt.compare(password + PEPPER, user.password)) {
          const { password: pwd, ...info } = user;
          return info;
        }
      }
      return null;
    } catch (err) {
      throw new Error(`Cannot Find user ${email}, ${err}`);
    }
  }

  // ? for validation purpose
  async validate(email: string, name?: string): Promise<TUser> {
    try {
      const conn = await Client.connect();
      const sql = `SELECT id,name,email,refreshtoken FROM users WHERE email=$1 ${
        name ? `OR name='${name + ''}'` : ''
      }`;
      const result = await conn.query(sql, [email]);
      conn.release();
      return result.rows[0];
    } catch (err) {
      throw new Error(`Cannot Find user ${email}, ${err}`);
    }
  }

  // ? for checking if user got fresh token or not
  async showByToken(value: string): Promise<TUser> {
    try {
      const conn = await Client.connect();
      const sql = `SELECT * FROM users WHERE $1=ANY(refreshToken)`;
      const result = await conn.query(sql, [value]);
      conn.release();
      return result.rows[0];
    } catch (err) {
      throw new Error(`Cannot Find user ${value}, ${err}`);
    }
  }

  // ? for removing the refresh token on logout
  async deleteRefreshToken(id: string): Promise<void> {
    try {
      const conn = await Client.connect();
      const sql = `UPDATE users SET refreshToken=NULL WHERE id=$1`;
      await conn.query(sql, [id]);
      conn.release();
    } catch (err) {
      throw new Error(`Cannot Find user ${id}, ${err}`);
    }
  }

  async storeToken(email: string, token: string[]): Promise<void> {
    try {
      const conn = await Client.connect();
      const sql = 'UPDATE users SET refreshToken=$1 WHERE email=$2';
      await conn.query(sql, [token, email]);
      conn.release();
    } catch (err) {
      throw new Error(`Couldn't insert user ${email} refresh token, ${err}`);
    }
  }

