import Client from '../database/client';

interface IToken {
  id?: number;
  userId: number;
  token: string;
  code: string;
  verified: boolean;
  createdAt?: Date;
}

export class Token {
  async createToken(
    token: string,
    code: string,
    userId?: number
  ): Promise<void> {
    try {
      const conn = await Client.connect();
      const sql =
        'INSERT INTO tokens (userid,token,code,createdat) VALUES ($1,$2,$3,$4)';
      await conn.query(sql, [
        userId,
        token,
        code,
        Date.now() + 15 * 60 * 1000 + '',
      ]);
      conn.release();
    } catch (err) {
      throw new Error(`Cannot create reset password token ${err}`);
    }
  }

  async getToken(id: string): Promise<IToken | null> {
    try {
      const conn = await Client.connect();
      const sql = 'SELECT * FROM tokens WHERE userid=$1';
      const result = await conn.query(sql, [id]);
      conn.release();
      if (+result.rows[0].createdat < Date.now()) return null;
      return result.rows[0];
    } catch (err) {
      throw new Error(`Cannot get the reset password token`);
    }
  }

  async verifiedCode(id: string): Promise<void> {
    try {
      const conn = await Client.connect();
      const sql = 'UPDATE tokens SET verified=TRUE WHERE userid=$1';
      await conn.query(sql, [id]);
      conn.release();
    } catch (err) {
      throw new Error(`Cannot get the reset password token`);
    }
  }

  async removeToken(id: string): Promise<void> {
    try {
      const conn = await Client.connect();
      const sql = 'DELETE FROM tokens WHERE userid=$1';
      await conn.query(sql, [id]);
      conn.release();
    } catch (err) {
      throw new Error(`An error happend while remove reset token`);
    }
  }
}

import { Application, NextFunction, Request, Response } from 'express';
import { unlink } from 'fs/promises';

import { Post } from '../model/post';
import upload from '../util/upload';
import optimizeImage from '../middleware/optimizeImage';
import verifyToken from '../middleware/verifyToken';
import validation from '../middleware/validation';
import APIError from '../Error/ApiError';

import {
  validatePostCreate,
  validatePostDelete,
  validatePostUpdate,
} from '../util/validators/postValidators';

const store = new Post();

const postsPerPage = 6;

const index = async (req: Request, res: Response): Promise<void> => {
  try {
    const author = Number(req.query.author) || null;
    const posts = await store.index(author);
    res.json({ message: 'retrived posts sucessfully', data: posts });
  } catch (err) {
    throw new Error(`couldn't get posts, ${err}`);
  }
};

const filter = async (req: Request, res: Response): Promise<void> => {
  try {
    const page = Number(req.query.page) || 1;
    const author = req.query.author as string | undefined;
    const query = (req.query.query as string) || '';
    const skip = (page - 1) * postsPerPage;
    const postsInfo = await store.filter(author, query, postsPerPage, skip);
    const postsCount = postsInfo.postsCount;
    const numberOfPages = Math.ceil(postsCount / postsPerPage);
    const next = page * postsPerPage < postsCount ? true : false;
    const prev = page > 1 ? true : false;
    res.status(200).json({
      message: 'retrived posts sucessfully',
      posts: postsInfo.posts,
      pagination: { postsCount, numberOfPages, page, next, prev },
    });
  } catch (err) {
    throw new Error(`couldn't get posts, ${err}`);
  }
};

const show = async (
  req: Request,
  res: Response,
  next: NextFunction
): Promise<void> => {
  try {
    const post = await store.show(req.params.postId);
    if (!post.post) {
      return next(
        new APIError(
          `couldn't find post ${req.params.postId}`,
          404,
          'failed to find the post',
          true
        )
      );
    }
    res.status(200).json({
      message: 'retrived post sucessfully',
      post: post.post,
      comments: post.comments,
    });
  } catch (err) {
    res.sendStatus(404);
  }
};
const create = async (req: Request, res: Response): Promise<void> => {
  try {
    const author = res.locals.username;
    const post = await store.create({ ...req.body, author });
    res.status(201).json({ message: 'post created sucessfully', data: post });
  } catch (err) {
    throw new Error(`couldn't create post , ${err}`);
  }
};

const update = async (
  req: Request,
  res: Response,
  next: NextFunction
): Promise<void | Response> => {
  try {
    const post = await store.update(
      req.params.postId,
      req.body,
      res.locals.username
    );
    if (!post?.post) {
      return next(
        new APIError(
          `couldn't find post ${req.params.postId} to update`,
          404,
          'failed to find the post',
          true
        )
      );
    }
    if (req.body.image) {
      try {
        await unlink(post.image);
      } catch (err) {
        return res
          .status(500)
          .json({ warning: "Image wasn't deleted, path error", data: post });
      }
    }
    res.json({ message: 'post updated sucessfully', data: post.post });
  } catch (err) {
    throw new Error(`couldn't updated post , ${err}`);
  }
};
const remove = async (
  req: Request,
  res: Response,
  next: NextFunction
): Promise<void> => {
  try {
    const post = await store.delete(req.params.postId, res.locals.username);
    if (!post) {
      return next(
        new APIError(
          `couldn't find post ${req.params.postId} to delete`,
          404,
          'failed to find the post',
          true
        )
      );
    }
    await unlink(post.image);
    res.json({ message: 'posts deleted sucessfully', data: post });
  } catch (err) {
    throw new Error(`couldn't delete post , ${err}`);
  }
};

const postRoutes = (app: Application) => {
  app
    .route('/posts')
    .get(verifyToken, index)
    .post(
      verifyToken,
      upload.single('image'),
      validatePostCreate(),
      validation,
      optimizeImage,
      create
    );

  app
    .route('/posts/:postId')
    .get(verifyToken, show)
    .delete(verifyToken, validatePostDelete(), validation, remove)
    .patch(
      verifyToken,
      upload.single('image'),
      validatePostUpdate(),
      validation,
      optimizeImage,
      update
    );

  app.get('/filter', verifyToken, filter);
};

export default postRoutes;

import Client from '../database/client';
import { TCommnet } from './comment';

export type TPost = {
  id?: number;
  title: string;
  description: string;
  field: string;
  image: string;
  author: string;
};

export class Post {
  async index(author: number | null): Promise<TPost[]> {
    try {
      const conn = await Client.connect();
      const sql = `SELECT * FROM posts ${
        author ? 'WHERE author=' + author : ''
      }`;
      const result = await conn.query(sql);
      conn.release();
      return result.rows;
    } catch (err) {
      throw new Error(`Cannot get posts ${err}`);
    }
  }

  async show(id: string): Promise<{ post: TPost; comments: TCommnet[] }> {
    try {
      const conn = await Client.connect();
      const sql = 'SELECT * FROM posts WHERE id=$1';
      const result = await conn.query(sql, [id]);
      const sql2 = 'SELECT * FROM comments WHERE postid=$1';
      const result2 = await conn.query(sql2, [id]);
      conn.release();
      return { post: result.rows[0], comments: result2.rows };
    } catch (err) {
      throw new Error(`Cannot get post ${id}, ${err}`);
    }
  }

  async showWithoutComments(id: string): Promise<TPost> {
    try {
      const conn = await Client.connect();
      const sql = 'SELECT * FROM posts WHERE id=$1';
      const result = await conn.query(sql, [id]);
      conn.release();
      return result.rows[0];
    } catch (err) {
      throw new Error(`Cannot get post ${id}, ${err}`);
    }
  }

  async update(
    id: string,
    post: TPost,
    username: string
  ): Promise<{ post: TPost; image: string } | null> {
    try {
      const conn = await Client.connect();
      const sql = 'SELECT author,image FROM posts WHERE id=$1';
      const result = await conn.query(sql, [id]);
      if (!result.rows[0] || result.rows[0].author !== username) {
        conn.release();
        return null;
      }
      const sql2 =
        'UPDATE posts SET title=$1,description=$2,image=$3, field=$4 WHERE id=$5 RETURNING *';
      const result2 = await conn.query(sql2, [
        post.title,
        post.description,
        post.image ? post.image : result.rows[0].image,
        post.field,
        id,
      ]);
      conn.release();
      return { post: result2.rows[0], image: result.rows[0].image };
    } catch (err) {
      throw new Error(`Cannot update post ${id}, ${err}`);
    }
  }

  async delete(id: string, username: string): Promise<TPost | null> {
    try {
      const conn = await Client.connect();
      const sql = 'SELECT author FROM posts WHERE id=$1';
      const result = await conn.query(sql, [id]);
      if (!result.rows[0] || result.rows[0].author !== username) {
        conn.release();
        return null;
      }
      const sql2 = 'DELETE FROM posts WHERE id=$1 RETURNING *';
      const result2 = await conn.query(sql2, [id]);
      conn.release();
      return result2.rows[0];
    } catch (err) {
      throw new Error(`Cannot delete post ${id}, ${err}`);
    }
  }

  async create(post: TPost): Promise<TPost> {
    try {
      const conn = await Client.connect();
      const sql =
        'INSERT INTO posts(title,description,field,image,author,readTime) VALUES ($1,$2,$3,$4,$5,$6) RETURNING *';
      const result = await conn.query(sql, [
        post.title,
        post.description,
        post.field,
        post.image,
        post.author,
        Math.ceil(post.description.split(' ').length / 100),
      ]);
      conn.release();
      return result.rows[0];
    } catch (err) {
      throw new Error(`Cannot create post `);
    }
  }

  // ? for filteration, search, and pagination
  async filter(
    author: string | undefined,
    query: string | undefined,
    limit: number,
    skip: number
  ): Promise<{ posts: TPost[]; postsCount: number }> {
    try {
      const whereQuery = `${author || query ? ' WHERE' : ''}`;
      const andQuery = `${author && query ? ' AND' : ''}`;
      const authorQuery = `${author ? ' author=' + `'${author}'` : ''}`;
      const searchQuery = `${query ? ` title ILIKE '%${query}%'` : ''}`;
      const myUltimateQuery = `SELECT * FROM posts${whereQuery}${authorQuery}${andQuery}${searchQuery}`;
      // ! NOTE if it work properly try to add these variables as $(variables) in the conn.query instead of putting all of it in the string
      const conn = await Client.connect();
      const sql = myUltimateQuery;
      const result = await conn.query(sql);
      const sql2 = `${myUltimateQuery} LIMIT $1 OFFSET $2`;
      const result2 = await conn.query(sql2, [limit, skip]);
      conn.release();
      return { posts: result2.rows, postsCount: result.rowCount };
    } catch (err) {
      throw new Error(`Cannot get posts ${err}`);
    }
  }
}

