import { VerifyErrors, verify } from 'jsonwebtoken';
import APIError from '../Error/ApiError';
import { User } from './../model/user';
import { Application, NextFunction, Request, Response } from 'express';
import signToken from '../util/signToken';

const {
  JWT_REFRESH_SECRET,
  JWT_REFRESH_EXPIRY,
  JWT_ACCESS_EXPIRY,
  JWT_SECRET,
} = process.env;

const store = new User();

const refreshToken = async (
  req: Request,
  res: Response,
  next: NextFunction
): Promise<void> => {
  try {
    if (!req.cookies['refresh-token']) {
      return next(
        new APIError(
          `You are not authorized to get such info, please login first`,
          401,
          'not authorized to get such a route',
          true
        )
      );
    }
    const jwtRefresh = req.cookies['refresh-token'];
    res.clearCookie('refresh-token', {
      httpOnly: true,
      maxAge: 24 * 60 * 60 * 1000,
    });
    const user = await store.showByToken(jwtRefresh);
    // ! remember to remove the refresh token from the database (make it one time use only)

    if (!user) {
      verify(
        jwtRefresh,
        JWT_REFRESH_SECRET as string,
        async (err: VerifyErrors | null, payload: any) => {
          if (err) return res.sendStatus(403);
          // ? with the refresh token rotation here we know that invalid token (resuse attempt), so we delete all user tokens
          await store.deleteRefreshToken(payload?.user.id);
        }
      );
      return next(
        new APIError(
          `cannot be accessed`,
          403,
          'failed to fetch the user info',
          true
        )
      );
    }
    const newRefreshTokens = user.refreshtoken?.filter(
      (rt) => rt !== jwtRefresh
    ) as Array<string>;

    verify(
      jwtRefresh,
      JWT_REFRESH_SECRET as string,
      async (err: VerifyErrors | null, payload: any) => {
        // ? HERE all good but expired token
        if (err) {
          await store.storeToken(user.email, newRefreshTokens);
        }
        if (err || payload?.user?.email !== user.email)
          return res.sendStatus(403);

        //  ? now we have a valid token
        const accessToken = signToken(
          payload?.user,
          JWT_SECRET + '',
          JWT_ACCESS_EXPIRY + ''
        );
        const newRefreshToken = signToken(
          payload?.user,
          JWT_REFRESH_SECRET + '',
          JWT_REFRESH_EXPIRY + ''
        );

        await store.storeToken(payload?.user.email, [
          ...newRefreshTokens,
          newRefreshToken,
        ]);

        res.cookie('refresh-token', newRefreshToken, {
          httpOnly: true,
          maxAge: 24 * 60 * 60 * 1000,
        });
        res.json({
          message: 'user token refreshed',
          name: user.name,
          accessToken,
        });
      }
    );
  } catch (err) {
    throw new Error(`couldn't authenticate user, ${req.body.email} , ${err}`);
  }
};

const logout = async (
  req: Request,
  res: Response,
  next: NextFunction
): Promise<void> => {
  try {
    if (!req.cookies['refresh-token']) {
      return next(
        new APIError(
          `You are not allowed do such an action, no token available.`,
          403,
          'not authorized to get such a route',
          true
        )
      );
    }
    const jwtRefresh = req.cookies['refresh-token'];
    res.clearCookie('refresh-token', {
      httpOnly: true,
      maxAge: 24 * 60 * 60 * 1000,
    });

    const user = await store.showByToken(jwtRefresh);
    if (!user) {
      return next(
        new APIError(
          `cannot be accessed`,
          204,
          'failed to fetch the user info',
          true
        )
      );
    }

    const newRefreshTokens = user.refreshtoken?.filter(
      (rt) => rt !== jwtRefresh
    ) as Array<string>;

    await store.storeToken(user.email, [...newRefreshTokens]);
    // IMPORTANT  don't forget in production to set secure flag to true in production
    res.sendStatus(204);
  } catch (err) {
    throw new Error(`couldn't authenticate user, ${req.body.email} , ${err}`);
  }
};

const refreshTokenRoute = (app: Application): void => {
  app.get('/token/refresh', refreshToken);
  app.get('/logout', logout);
};

export default refreshTokenRoute;

import { Application, Request, Response } from 'express';
import dotenv from 'dotenv';
import { OAuth2Client } from 'google-auth-library';
import { decode } from 'jsonwebtoken';
import { User } from '../model/user';
import signToken from '../util/signToken';

dotenv.config();

const {
  GOOGLE_AUTH_CLIENTID,
  GOOGLE_AUTH_SECRET,
  JWT_SECRET,
  JWT_ACCESS_EXPIRY,
  JWT_REFRESH_SECRET,
  JWT_REFRESH_EXPIRY,
} = process.env;

const store = new User();

const oAuth2Client = new OAuth2Client(
  GOOGLE_AUTH_CLIENTID,
  GOOGLE_AUTH_SECRET,
  'postmessage'
);

const googleAuth = async (
  req: Request,
  res: Response
): Promise<void | Response> => {
  try {
    const { code } = req.body;
    const { tokens } = await oAuth2Client.getToken(code);
    const payload = decode(tokens.id_token + '') as {
      email: string;
      given_name: string;
      family_name: string;
    };
    let user = await store.validate(payload.email);
    if (!user) {
      const randomNumber = Math.floor(1000 + Math.random() * 9000);
      user = await store.googleAuthRegister(
        payload.email,
        `${payload.given_name} ${payload.family_name}#${randomNumber}`
      );
    }
    // NOTE  we may check if user has a cookie, so we replace it
    const { id, name, email } = user;
    const token = signToken(
      { id, name, email },
      JWT_SECRET + '',
      JWT_ACCESS_EXPIRY + ''
    );
    const refreshToken = signToken(
      { id, name, email },
      JWT_REFRESH_SECRET + '',
      JWT_REFRESH_EXPIRY + ''
    );
    if (!user.refreshtoken) {
      user.refreshtoken = [];
    }
    await store.storeToken(email, [...user.refreshtoken, refreshToken]);
    res.cookie('refresh-token', refreshToken, {
      httpOnly: true,
      maxAge: 24 * 60 * 60 * 1000,
    });

    res.status(200).json({ name: user.name, accessToken: token });
  } catch (err) {
    res.sendStatus(500);
  }
};

